    public ArrayList<English> getEn(String key){
        ArrayList<English> englishes = new ArrayList<English>();
        English english;
        String command = "SELECT * FROM ru2en WHERE " +
                "ki1=\""+key+"\" OR " +
                "mod1=\""+key+"\" OR " +
                "ki2=\""+key+"\" OR " +
                "mod2=\""+key+"\" OR " +
                "ki3=\""+key+"\" OR " +
                "mod3=\""+key+"\"";
        Cursor cursor = this.getReadableDatabase().rawQuery(command, null);
        cursor.moveToFirst();
        while(! cursor.isAfterLast()) {
            english = new English(
                    cursor.getString(1),
                    cursor.getString(2),
                    cursor.getString(3),
                    cursor.getString(4),
                    cursor.getString(5),
                    cursor.getString(6),
                    cursor.getString(7),
                    cursor.getString(8),
                    cursor.getString(9),
                    cursor.getString(10));
            englishes.add(english);
            cursor.moveToNext();
        }
        cursor.close();
        return  englishes;
    }
    public ArrayList<Kirundi> getRun(String key){
        ArrayList<Kirundi> kirundis = new ArrayList<Kirundi>();
        Kirundi kirundi;
        String command = "SELECT * FROM en2ru WHERE en=\""+key+"\"";
        Cursor cursor = this.getReadableDatabase().rawQuery(command, null);
        cursor.moveToFirst();
        while(! cursor.isAfterLast()) {
            kirundi = new Kirundi(cursor.getString(1), cursor.getString(2));
            kirundis.add(kirundi);
            cursor.moveToNext();
        }
        cursor.close();
        return  kirundis;

    }

